sequence.script:
var MRS_EXCEEDED = false;
var MRS_CONTENT_LENGTH = mc.getProperty("MRS_FUNC_CONTENT").length() / 1.36;

if (MRS_CONTENT_LENGTH > mc.getProperty("MRS_FUNC_MAX_REQUEST_SIZE")){
MRS_EXCEEDED = true;
}

mc.setProperty('MRS_CONTENT',mc.getProperty("MRS_FUNC_CONTENT"));
mc.setProperty('MRS_CONTENT_LENGTH',MRS_CONTENT_LENGTH);
mc.setProperty('MRS_EXCEEDED',MRS_EXCEEDED);
